import { createGlobalStyle } from 'styled-components';

const about_style = createGlobalStyle`

/* ----------------------About style section---------------------- */

.about-heading{
    color:#fba62f;
    margin-top:2%;
}

@media only screen and ( max-width:980px) {
    .main-about{
        padding-top:10%
    }
}

`;
export default about_style;
