    transform: skewX(-25deg);
    padding-top:15%;
    left: 15%;
    position: relative;
    cursor: pointer;
    display: table-cell;
    border-bottom: 2px dotted purple;
    border-left: 2px dotted purple;
    <border-gap></border-gap>;
  }
  .h-c:hover{
    text-shadow: none;
    transform: none;
    transition: 1s;
    color:#494b2f;
