import React, { Component } from 'react'

export default class HideShow extends Component {
    constructor(){
        super();
        this.state={
            show:true
        }
    }
    render() {
        return (
            <div>

                <button onClick={()=>{this.setState({show:!this.state.show})}}>toggle</button>
            </div>
        );
    }
}
