import { createGlobalStyle } from 'styled-components';

const Services = createGlobalStyle`
  .contect{
    margin: 0%;
    padding: 0%;
    background: #8a8787; 
  }
  .h-c{
    font-size: 20px;
    color: #494b2f;
    text-shadow:0px -18px 2px #d8cdcd4c;
    transform: skewX(-25deg);
    padding-top:4%;
    cursor: pointer;
  }
  .h-c:hover{
    text-shadow: none;
    transform: none;
    transition: cubic-bezier(0.075, 0.82, 0.165, 1);
  }
`;
export default Services;
