import CWU from './Unmout';

export default class LifeCycle extends Component {
    constructor(){
        super();
        this.state={
            active:null,
            who:null,
            toggle:null
        }
    }
    // tis method is very use in api call 
    componentDidMount(){

    }
    //  This method is a very use to update state 
    componentDidUpdate(){
        if(this.state.who==null){
            this.setState({who:"by piyush kumar"})
        }
    }
    // this method is called to delete the component
    componentWillUnmount(){

    }
    render() {
        return (
            <div>
                <h1>component did update {this.state.who}</h1>
                <button className="btn" onClick={()=>{this.setState({active:'yes'})}}>active</button>

                <h1>component unmount</h1>
                {
                    this.state.toggle
                }
            </div>
        )
    }
}
