import { createGlobalStyle } from 'styled-components';

const GlobleStyle = createGlobalStyle`
    :root{
        --purple-primary :hsl(243,69,59);
        --accent-pink :hsl(345,88,63);
        --natural-light :hsl(222,100,97);
        --lavender-secondary :hsl(237,21,52); /*primary font color*/
        --dark-primary : hsl(237,56,20);
        --border-color :hsl(260,68,83);
    }

    *{
        margin:0;
        padding:0;
        list-style:none;
        box-sizing:norder-box;

    
    }
    body {
        background-color :#ccc;
    }
    .navbar{
        height:60px;
        background-color:var(--purple-primary);
        margin:0;
        margin-bottom :0% ;
        color :#ffff;
    }
    .slider{
        color:#fff;
        height:200px;
        background-color:black;
    }
    
    .got-started{
        height:400px;
        background:red;
    }
    .services{
        height:500px;
        background:#ccc;
    }
    .quality{
        height:500px;
        background-color:purple;
    }
    .clients{
        height:300px;
        background-color:orange;
    }
    // CONTECT US
    .contect{
        height :200px;
        background-color:white;
    }
    /* .h-c{
        text-align:center;
    } */
    .fa-thin{
        color:black;
    }
    .fontawesome{
        color:blue;
        font-size:30px;
        margin-top:2%;
        margin-left:3%;
    }
    .span{
        margin-left:2%;
        margin-top:-7%;
        color:red;
    }

    // end contect us section
    .footer{
        height:100px;
        background-color:#ccc;
    }
`;
export default GlobleStyle;
