import { createGlobalStyle } from 'styled-components';

const GlobleStyle = createGlobalStyle`
body{
    background-color:#9266ff;
}
.home{
    background-color:#3f3160;
    min-height:500px;
    width:400px;
    margin:40px auto;
}
#to-do-from input{
    background-color:rgb(27,112,137);
    border:0;
    width:200px;
    height:50px;
    pading:0 20px;
    margin:20px;
    font-size:18px;
    border-radius:10px;
    color:#fff;
    padding:0 0 0 10px;
}
#to-do-from input::placeholder{
    color:rgba(255,255,255,0.5);
}
#to-do-from input:focus{
    outline:none;
}
#to-do-from button{
    height:50px;
    width:80px;
    background-color:rgb(202,156,24);
    cursor:pointer;
    font-weight:bolder;
    color:#fff;
    border:none;
    border-radius:50px;
}
`;
export default GlobleStyle;
