import React, { Component } from 'react'

export default class LifeCycle extends Component {
    constructor(){
        super();
        this.state={
            active:null,
            who:"123"
        }
    }
    // tis method is very use in api call 
    componentDidMount(){

    }
    //  This method is a very use to update state 
    componentDidUpdate(){
        if(this.state.who=null){
            this.setState({who:"piyush kumar"})
        }
    }
    // this method is called to delete the component
    componentWillUnmount(){

    }
    render() {
        return (
            <div>
                <h1>component did update in {this.state.who}</h1>
                <button className="btn" onClick={()=>{this.setState({active:'yes'})}}>active</button>
            </div>
        )
    }
}
