import { Carousel } from 'react-bootstrap';
import Image1 from './Image/slider-3.jpg';
// import Image2 from './Image/slider-2.jpg';
// import Image3 from './Image/slider-1.jpg';
// import Image4 from './Image/slider-4.jpg';

class Slider extends React.Component {
  render() {
    // const slider ={
    //     background:"pink",
    //     color:"#fff",
    //     innerHeight:100
    // }
    return (
      // <div style={slider}>
        <Carousel>
          <Carousel.Item interval={2000}>
            <img src={Image1} alt="" className="d-block w-100" />
            <Carousel.Caption>
              <h1> slider</h1>
              <p>this is first slider</p>
            </Carousel.Caption>
          </Carousel.Item>

          <Carousel.Item interval={2000}>
            <img src={Image1} alt="" className="d-block w-100" />
            <Carousel.Caption>
              <h1>second slider</h1>
              <p>this is first slider</p>
            </Carousel.Caption>
          </Carousel.Item>

          <Carousel.Item interval={2000}>
            <img src={Image1} alt="" className="d-block w-100" />
            <Carousel.Caption>
              <h1>second slider</h1>
              <p>this is first slider</p>
            </Carousel.Caption>
          </Carousel.Item>

          <Carousel.Item interval={2000}>
            <img src={Image1} alt="" className="d-block w-100" />
            <Carousel.Caption>
              <h1>second slider</h1>
              <p>this is first slider</p>
            </Carousel.Caption>
          </Carousel.Item>

        </Carousel>
      // </div>
    );
  }
}

export default Slider ;
