import React, { Component } from 'react'

export default class Fvalidation extends Component {
    constructor(){
        super();
        this.state={
            name:" ",
            Password:" ",
            nameerror:" ",
            Passworderror:" "
        }
    }
    valid(){
        if(!this.state.name.includes("@")&& this.state.Password.lenght<5){
            this.setState({
                nameerror:"invlid name",
                Passworderror:"invilid password"
            })
        }
    }
    submit(){
        this.setState({
            name:" ",
            Password: " "
        })
        if(this.valid()){
            alert ("form has submitted");
        }
    }
    render() {
        return (
            <div>
                <h1>Form Validation</h1>
                <form action="">
                    <input type="text"
                    name="user"
                    onChange={(e)=>{
                        this.setState({name:e.target.value})
                    }} /> 
                    <p>{this.state.nameerror}</p>
                    <br /> <br />

                    <input type="Password"
                    name="Password"
                    onChange={(e)=>{
                        this.setState({Password:e.target.value})
                    }} />
                    <p>{this.state.Passworderror}</p>
                    <br /> <br />

                    <button onClick={()=>this.submit()}>Submit</button>

                </form>
            </div>
        )
    }
}
